let scrollBtn = () => {
  let scrollBtn = document.getElementById("scroll-up");

  this.scrollY >= 150
    ? scrollBtn.classList.add("show-scroll-btn")
    : scrollBtn.classList.remove("show-scroll-btn");
};

window.addEventListener("scroll", scrollBtn);

const btnOpen = document.querySelector(".open"),
  menuList = document.querySelector(".menu"),
  btnClose = document.querySelector(".close");
btnOpen.addEventListener("click", () => {
  menuList.classList.toggle("menu--active");
});

document.querySelectorAll(".phone") &&
  document
    .querySelectorAll(".phone")
    .forEach((phoneInput) =>
      IMask(phoneInput, { mask: "+{38} (000) 000-00-00" })
    );
